 MODULE tt;  FROM SYSTEM IMPORT ASSEMBLER;  PROCEDURE ToSuper;"BEGIN$ASSEMBLER(MOVEM.L D1/D2/A1/A2,-(A7)(MOVEQ   #1,D0(MOVE.L  D0,-(A7)        ; Super (1)(MOVE    #$20,-(A7)(TRAP    #1(TST     D0(BNE     wasSuper(; wir sind im User-Mode(MOVE.W  D0,4(A7)        ; Super (0)(TRAP    #1(ADDQ.L  #6,A7(MOVEM.L (A7)+,D1/D2/A1/A2(MOVE.L  (A7)+,A0        ; RÅcksprungadr. nach A0(MOVE.L  A7,USP(MOVE.L  D0,A7(MOVE    SR,D0(ANDI    #$CFFF,D0(JMP     (A0)$wasSuper(; wir sind schon im Supervisor-Mode(ADDQ.L  #6,A7(MOVE    SR,D0(MOVEM.L (A7)+,D1/D2/A1/A2$END"END ToSuper;  PROCEDURE bla;"BEGIN$HALT"END bla;" BEGIN"(*"ASSEMBLER(BREAK(JSR     ToSuper(BREAK"END"*)"bla;"bla;"bla END tt. 
